INSERT INTO `aktivitet` VALUES ('träff', '"Att ställa ut sin katt"<br>för nya utställare', 'ny_utst', '2004-05-16', '13.00', NULL, '2004-05-09', NULL, 97, '101', '<b>&quot;Att st&auml;lla ut sin katt&quot;</b><br> &auml;r fr&auml;mst \r\n  t&auml;nkt f&ouml;r nya utst&auml;llare.<br>\r\n  Vi tr&auml;ffas under enkla och  opretenti&ouml;sa former f&ouml;r att  g&aring; igenom hur det g&aring;r till, <br>vad som kr&auml;vs och lite praktiska ting kring <br>\r\n  temat utst&auml;llning.<br>\r\n  Exempel p&aring; inneh&aring;ll:<br>\r\n  vilka katter kan st&auml;llas ut?<br>\r\n  hur anm&auml;ler man?<br>\r\n  f&ouml;rberedelser<br>\r\n  sj&auml;lva utst&auml;llningen</p>\r\n<p><b>Plats : Katrinelundsg 4A, Malmö<br>(sidan om Willy:s)</b></p>\r\n<p><b>V&Auml;LKOMNA !</b></p>');
