INSERT INTO `artikel` VALUES (10, '&Aring;rets Klubbkatt 1999', '', NULL, NULL, 'se', NULL, NULL, NULL, '2003-06', '<P>UNGDJUR/JUNIOR/VUXEN</P>\r\n<P>1. CH S*Citizen\'s Jade N.F.S., SBI n 135p<BR>\r\n  &Auml;gare: Catarina H&aring;kansson</P>\r\n<P>2. S*Tintomara\'s Zara Z&aacute;hara, BUR n 135p<BR>\r\n  &Auml;gare: Ilga Liljenberg</P>\r\n<P>3. IC S*Citizen\'s Electra, SBI a 130p<BR>\r\n  &Auml;gare: Lena Harsten</P>\r\n<P>4. S*Citizen\'s Oksana N.F.S., SBI a 21 125p<BR>\r\n  &Auml;gare: Catarina H&aring;kansson</P>\r\n<P>5. CH S*Citizen\'s Ice Queen, SBI c 125p<BR>\r\n  &Auml;gare: Lena Harsten</P>\r\n<P>6. EC S*Tintomara\'s Tara Tajger, BUR n 120p<BR>\r\n  &Auml;gare: Ilga Liljenberg</P>\r\n<P>7. S*Citizen\'s Make My Day, SBI n21 115p<BR>\r\n  &Auml;gare: Lena Harsten</P>\r\n<P>8. IC S*Citizen\'s Tanita Tikaram, SBI n 110p<BR>\r\n  &Auml;gare: Catarina H&aring;kansson</P>\r\n<P>9. IC S*Sargenta Silver Nickelby, BRI ns2264 105p<BR>\r\n  &Auml;gare: Gun Hall</P>\r\n<P><BR>\r\n  10. GIC S*Tintomara\'s Qarmen, BUR n 100p<BR>\r\n  &Auml;gare: Ilga Liljenberg</P>\r\n<P>11. EC S*Grazette\'s Lac\'Rima, SBI a 100p<BR>\r\n  &Auml;gare: Catarina H&aring;kansson</P>\r\n<P>12. GIC S*Ullstrumpan\'s Dajm, SBI n 100p<BR>\r\n  &Auml;gare: Catarina H&aring;kansson</P>\r\n<P>13. EC S*Ta\'Chira Ogopogo, DM, SBI a 90p<BR>\r\n  &Auml;gare: Catarina H&aring;kansson</P>\r\n<P>14. GIC S*Wi\'nte Sams Claire de Lune, TUA g23 89p<BR>\r\n  &Auml;gare: Kerstin Barreg&aring;rd</P>\r\n<P>15. S*Tintomara\'s Zunny Girl, BUR n 89p<BR>\r\n  &Auml;gare: Ilga Liljenberg</P>\r\n<P>16. CH S*Lejongapet\'s Anders Andapsin, TUV d 70p<BR>\r\n  &Auml;gare: Anette Hansson &amp; Leif Andersson</P>\r\n<P>17. IC S*Tufvans Ariadne, TUV d 26p<BR>\r\n  &Auml;gare: Anette Hansson &amp; Leif Andersson</P>\r\n<P>KASTRAT</P>\r\n<P>1. EP &amp; EC S*Finnsj&ouml;\'s Salome, BUR n 135p<BR>\r\n  &Auml;gare: Ilga Liljenberg</P>\r\n<P><BR>\r\n  2. EP &amp; IC S*Citizen\'s Olympic Obelix, SBI e 105p<BR>\r\n  &Auml;gare: Catar